import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
      this.state = {
        fields: {},
        errors: {}
      }
  }
  handleValidation(){
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;
    //Validate Name
    if(!fields["name"]){
      formIsValid = false;
      errors["name"] = "Để trống cc à";
    }
  }

  handleChange(field, e){
    let fields = this.state.fields;
    fields[fields]  = e.target.value;
    this.setState({
      fields: fields
    })
  }
  render() {
    //console.log("fuck----",errors);
    return (
      <div className="main">

        <div className="container">
            <div className="signup-content">
                <form  className="signup-form">
                    <h2>Sign up </h2>
                    <div className="form-group">
                        <input type="text" className="form-input" name="name" placeholder="Your Name" onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]}/>
                    </div>
                    <div className="form-group">
                        <input type="email" className="form-input" name="email" placeholder="Email"/>
                    </div>
                    <div className="form-group">
                        <input type="number" className="form-input" name="phone"  placeholder="Phone number"/>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="address" placeholder="Address"/>
                    </div>
                    <div className="form-group dpl-flex">
                      <div className="mgr30">
                        <input type="radio" 
                             name="male" value="Male" />
                        <label >Male</label>
                      </div>

                      <div>
                        <input type="radio"  
                             name="female" value="Female" />
                        <label >Female</label>
                      </div>

                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="password" placeholder="Password"/>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="re-password" placeholder="Confirm password"/>
                    </div>
                    <div className="form-group">
                        <input type="checkbox" name="agree-term" id="agree-term" className="agree-term" />
                        <label for="agree-term" className="label-agree-term"><span><span></span></span>I agree all statements in  <a href="#" className="term-service">Terms of service</a></label>
                    </div>
                    <div className="form-group">
                        <input type="submit" name="submit" id="submit" className="form-submit submit" value="Sign up" />
                        <a href="#" className="submit-link submit">Sign in</a>
                    </div>
                </form>
            </div>
        </div>

    </div>
    );
  }
}

export default App;
