import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
      this.state = {
        fields: {},
        errors: {}
      }
  }
  handleValidation(){
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    //Validate Name
    if(!fields["name"]){
      formIsValid = false;
      errors["name"] = "Để trống cc à!";
    }else{
      if(fields["name"].length > 160){
        errors["name"] = "Viết dưới 160 từ thôi!";
      }
      if(fields["name"].length < 8){
        errors["name"] = "Viết nhiều chữ lên đi!"
      }
      if(fields["name"].indexOf(' ') >= 0){
        errors["name"] = "Viết liền vào đê !"
      }
      let pattern = /(^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[-_]))+/gm;
      if(!pattern.test(fields["name"])) {
          errors["name"] = "Phải bao gồm cả chữ và số _ hoặc -";
      }

    }

    if(!fields["email"]){
      formIsValid = false;
      errors["email"] = "Để trống cc à!"
    }else{
      let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
      if(!re.test(fields["email"])){
        errors["email"] = "Méo đúng định dạng email!"
      }    
    }

    if(!fields["phone"]){
      formIsValid = false;
      errors["phone"] = "Để trống cc à!"
    }else{
      i
    }

    if(!fields["address"]){
      formIsValid = false;
      errors["address"] = "Để trống cc à!"
    }

    if(!fields["password"]){
      formIsValid = false;
      errors["password"] = "Để trống cc à!"
    }


    this.setState({
      errors: errors
    })

  }

  contactSubmit(e){
        e.preventDefault();

        if(this.handleValidation()){
           alert("Form submitted");

        }else{
          //alert("Form has errors.")
        }

    }

  handleChange(field, e){
    console.log(e.target.value);
    let fields = this.state.fields;
    fields[field]  = e.target.value;
    this.setState({
      fields: fields
    })
  }

  render() {
    //console.log("fuck----");
    return (
      <div className="main">

        <div className="container">
            <div className="signup-content">
                <form  className="signup-form" >
                    <h2>Sign up </h2>
                    <div className="form-group">
                        <input type="text" className="form-input" name="name" placeholder="Your Name" onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]}/>
                        <span style={{color: "#920808"}}>{this.state.errors["name"]}</span>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="email" placeholder="Email" onChange={this.handleChange.bind(this,"email")} value={this.state.fields["email"]}/>
                        <span style={{color: "#920808"}}>{this.state.errors["email"]}</span>
                    </div>
                    <div className="form-group">
                        <input type="number" className="form-input" name="phone"  placeholder="Phone number" onChange={this.handleChange.bind(this, "phone")}/>
                         <span style={{color: "#920808"}}>{this.state.errors["phone"]}</span>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="address" placeholder="Address" onChange={this.handleChange.bind(this, "address")}/>
                        <span style={{color: "#920808"}}>{this.state.errors["address"]}</span>
                    </div>
                    <div className="form-group dpl-flex">
                      <div className="mgr30">
                        <input type="radio" 
                             name="male" value="Male" />
                        <label >Male</label>
                      </div>

                      <div>
                        <input type="radio"  
                             name="female" value="Female" />
                        <label >Female</label>
                      </div>

                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="password" placeholder="Password" onChange={this.handleChange.bind(this, "password")}/>
                        <span style={{color: "#920808"}}>{this.state.errors["password"]}</span>
                    </div>
                    <div className="form-group">
                        <input type="text" className="form-input" name="re-password" placeholder="Confirm password"/>
                        <span style={{color: "#920808"}}>{this.state.errors["re-password1"]}</span>
                    </div>
                    <div className="form-group">
                        <input type="checkbox" name="agree-term" id="agree-term" className="agree-term" />
                        <label htmlFor="agree-term" className="label-agree-term"><span><span></span></span>I agree all statements</label>
                    </div>
                    <div className="form-group">
                        <button onClick={this.contactSubmit.bind(this)} className="form-submit submit" value="">Sign up</button>
                        <a href="#" className="submit-link submit">Sign in</a>
                    </div>
                </form>
            </div>
        </div>

    </div>
    );
  }
}

export default App;
